import React, { Component } from "react";
import libroService from "../services/libro-service";

export default class Libro extends Component {
  constructor(props) {
    super(props);
    this.getLibros = this.getLibros.bind(this);
    this.refreshLibros = this.refreshLibros.bind(this);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);
    this.addNew = this.addNew.bind(this);
    this.newLibros = this.newLibros.bind(this);
    this.deleteProduct = this.deleteProduct.bind(this);
    this.getProduct = this.getProduct.bind(this);

    this.state = {
        products: [],
        id: "",
        name: "",
        price: "", 
        isDeleted: false,
        submitted: false,
        isUpdate: false
      };
  }

  componentDidMount() {
    this.getLibros();
  }

  getLibros() {
    libroService.getAllLibros()
      .then(response => {
        this.setState({
          libros: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  refreshLibros() {
    this.getLibros();
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value
    });
  }

  onChangePrice(e) {
    this.setState({
      price: e.target.value
    });
  }

  addNew() {
    var data = {
      id: this.state.id,
      name: this.state.name,
      price: this.state.price
    };

    if (this.state.isUpdate === false){
      libroService.addNewLibros(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          name: response.data.name,
          price: response.data.price,
          isDeleted: response.data.isDeleted,
          submitted: true
        });
        console.log(response.data);
        this.refreshLibros();
      })
      .catch(e => {
        console.log(e);
      });
    } else {
      libroService.update(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          name: response.data.name,
          price: response.data.price,
          isDeleted: response.data.isDeleted,
          submitted: true, 
          isUpdate: true
        });
        console.log(response.data);
        this.refreshProducts();
      })
      .catch(e => {
        console.log(e);
      });
    }
  }

  newLibros() {
    this.setState({
      id: "",
      name: "",
      price: "",
      isDeleted: false,
      submitted: false
    });
    this.refreshLibros();
  }

  deleteLibros(id){
    var data = {
      id: id
    };
    console.log("Data libros : ", data);

    libroService.deleteLibro(id)
      .then(response => {
        this.setState({
          id: response.data.id,
          name: response.data.name,
          price: response.data.price,
          isDeleted: true
        });
        this.refreshLibros();
      })
      .catch(exception => {
        console.log(exception);
      });
  }

  getLibros(id){
    var data = {
      id: id
    };
    console.log("Data libros : ", data);

    libroService.getOneLibro(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          name: response.data.name,
          price: response.data.price
        });
        this.refreshProducts();
      })
      .catch(exception => {
        console.log(exception);
      });
  }

  render() {
    const { libros } = this.state;
    return (
      <><div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>Successfully submitted!</h4>
            <button className="btn btn-success" onClick={this.newLibros}>Add New One</button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input type="text" className="form-control" id="name" required value={this.state.name} onChange={this.onChangeName} name="name" />
            </div>

            <div className="form-group">
              <label htmlFor="price">Price</label>
              <input type="text" className="form-control" id="price" required value={this.state.price} onChange={this.onChangePrice}name="price" />
            </div>
            <br></br>
            <button onClick={this.addNew} className="btn btn-success">
              Submit
            </button>
          </div>
        )}
      </div>
      <br></br>
      <br></br>
      <div className="list row">
          <div className="col-md-6">
            <h4> Libros habilitados</h4>
            <br></br>
            <table className="table table-striped table-bordered">
              <thead>
                <tr>
                  <th> Id</th>
                  <th>Titulo</th>
                  <th>Autor</th>
                  <th>DisponibleParaPrestamo</th>
                </tr>
              </thead>
              <tbody>
                {libros && libros.map(libro => <tr key={libro.id}>
                  <td>{product.id}</td>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td><button className="btn btn-success" onClick={() => this.getProduct(product.id)}>Update</button></td>
                  <td><button className="btn btn-danger" onClick={() => this.deleteProduct(product.id)}>Delete</button></td>
                </tr>
                )}
              </tbody>
            </table>
          </div>
        </div></>
    );
  }
}
