import React, { Component } from "react";
import libroService from "../services/libro-service";

export default class Libro extends Component {
  constructor(props) {
    super(props);
    this.getLibros = this.getLibros.bind(this);
    this.refreshLibros = this.refreshLibros.bind(this);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);
    this.addNew = this.addNew.bind(this);
    this.newLibros = this.newLibros.bind(this);
    this.deleteProduct = this.deleteProduct.bind(this);
    this.getProduct = this.getProduct.bind(this);

    this.state = {
        libros: [],
        id: "",
        titulo: "",
        autor: "", 
        disponibleParaPrestamo: true,
        submitted: false
      };
  }

  componentDidMount() {
    this.getLibros();
  }

  getLibros() {
    libroService.getAllLibros()
      .then(response => {
        this.setState({
          libros: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  refreshLibros() {
    this.getLibros();
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value
    });
  }

  onChangePrice(e) {
    this.setState({
      price: e.target.value
    });
  }

  addNew() {
    var data = {
      id: this.state.id,
      name: this.state.name,
      price: this.state.price
    };

