      libroService.addNewLibros(data)
      .then(response => {
        this.setState({
          id: response,
          submitted: true
        });
        console.log(response.data);
        this.refreshLibros();
      })
      .catch(e => {
        console.log(e);
      });
 //   } 
    // else {
    //   libroService.update(data)
    //   .then(response => {
    //     this.setState({
    //       id: response.data.id,
    //       name: response.data.name,
    //       price: response.data.price,
    //       isDeleted: response.data.isDeleted,
    //       submitted: true, 
    //       isUpdate: true
    //     });
    //     console.log(response.data);
    //     this.refreshProducts();
    //   })
    //   .catch(e => {
    //     console.log(e);
    //   });
    // }
  }

  newLibros() {
    this.setState({
      id: "",
      titulo: "",
      autor: "", 
      disponibleParaPrestamo: true,
      submitted: false
    });
    this.refreshLibros();
  }

  deleteLibros(id){
    var data = {
      id: id
    };
    console.log("Data libros : ", data);

    libroService.deleteLibro(id)
      .then(response => {
        // this.setState({
        //   id: response.data.id,
        //   name: response.data.name,
        //   price: response.data.price,
        //   isDeleted: true
        // });
        this.refreshLibros();
      })
      .catch(exception => {
        console.log(exception);
      });
  }

  getLibros(id){
    var data = {
      id: id
    };
    console.log("Data libros : ", data);

    libroService.getOneLibro(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          titulo: response.data.titulo,
          autor: response.data.autor,
          disponibleParaPrestamo:response.data.disponibleParaPrestamo
        });
        this.refreshLibros();
      })
      .catch(exception => {
        console.log(exception);
      });
  }

  render() {
    const { libros } = this.state;
    return (
      <><div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>Successfully submitted!</h4>
            <button className="btn btn-success" onClick={this.newLibros}>Add New One</button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="name">Titulo</label>
              <input type="text" className="form-control" id="titulo" required value={this.state.titulo} onChange={this.onChangeName} name="name" />
            </div>

            <div className="form-group">
              <label htmlFor="price">Price</label>
              <input type="text" className="form-control" id="price" required value={this.state.autor} onChange={this.onChangePrice}name="price" />
            </div>
            <br></br>
            <button onClick={this.addNew} className="btn btn-success">
              Submit
            </button>
          </div>
        )}
      </div>
      <br></br>
      <br></br>
      <div className="list row">
          <div className="col-md-6">
            <h4> Libros habilitados</h4>
            <br></br>
            <table className="table table-striped table-bordered">
              <thead>
                <tr>
                  <th> Id</th>
                  <th>Titulo</th>
                  <th>Autor</th>
                  <th>DisponibleParaPrestamo</th>
                </tr>
              </thead>
              <tbody>
                {libros && libros.map(libro => <tr key={libro.id}>
                  <td>{libro.id}</td>
                  <td>{libro.titulo}</td>
                  <td>{libro.autor}</td>
                  <td>{libro.disponibleParaPrestamo}</td>
                  <td><button className="btn btn-success" onClick={() => this.getProduct(product.id)}>asa</button></td>
                  <td><button className="btn btn-danger" onClick={() => this.deleteProduct(product.id)}>Delete</button></td>
                </tr>
                )}
              </tbody>
            </table>
          </div>
        </div></>
    );
  }
}
